import React, {useEffect, useState} from "react";
import {Link} from "react-router-dom";
import {apiGet, apiPost, apiPut} from "../utils/api";


const PersonStatistics = () => {
    const [statistics, setStatistics] = useState();
   
    useEffect(() => {
        apiGet("/api/people/statistics").then((data) => setStatistics(data));
    }, []);
    
   
    

    if(!statistics)
        return <>Loading...</>
    return (
   
        <div>
            
            <p>
                <strong>Celková cena za rok:</strong>
                <br/>
                {statistics.personId}
                <br/>
                <strong>Celková cena za celou dobu:</strong>
                <br/>
                {statistics.personName}
                <br/>
                <strong>počet faktur:</strong>
                <br/>
                {statistics.revenue}
            </p>

            <table className="table table-bordered">
                <thead>
                <tr>
                    <th>#</th>
                    <th>Jméno</th>
                    <th colSpan={3}>Akce</th>
                </tr>
                </thead>
                <tbody>
                {items.map((item, index) => (
                    <tr key={index + 1}>
                        <td>{index + 1}</td>
                        <td>{item.personName}</td>
                        <td>
                            {item.revenue}
                        </td>
                    </tr>
                ))}
                </tbody>
            </table>

        </div>
        
    );
};

export default PersonStatistics;
