import React, {useEffect, useState} from "react";
import {Link} from "react-router-dom";
import {apiGet, apiPost, apiPut} from "../utils/api";


const InvoicesStatistics = () => {
    const [statistics, setStatistics] = useState();
   
    useEffect(() => {
        apiGet("/api/invoices/statistics").then((data) => setStatistics(data));
    }, []);
   
    

    if(!statistics)
        return <>Loading...</>
    return (
   
        <div> 
            <p>
                <strong>Celková cena za rok:</strong>
                <br/>
                {statistics.currentYearSum}
                <br/>
                <strong>Celková cena za celou dobu:</strong>
                <br/>
                {statistics.allTimeSum}
                <br/>
                <strong>počet faktur:</strong>
                <br/>
                {statistics.invoicesCount}
            </p>

            <table class="table">
                <thead>
                    <tr>
                    <th scope="col">#</th>
                    <th scope="col">Celková cena za rok:</th>
                    <th scope="col">Celková cena za celé období</th>
                    <th scope="col">Počet faktur</th>
                    </tr>
                </thead>
                <tbody>
                    <tr>
                    <th scope="row"></th>
                    <td>{statistics.currentYearSum}</td>
                    <td>{statistics.allTimeSum}</td>
                    <td>{statistics.invoicesCount}</td>
                    </tr>
                </tbody>
                </table>
        </div>
        
        
    );
};

export default InvoicesStatistics;
