import React, {useEffect, useState} from "react";
import {useParams} from "react-router-dom";

import {apiGet} from "../utils/api";


const InvoicesDetail = () => {
    const {id} = useParams();
    const [invoice, setInvoice] = useState();
    
    

    useEffect(() => {
        apiGet("/api/invoices/" + id).then((data) => setInvoice(data));
    }, [id]);
    

if(!invoice)
    return <>Loading...</>

    return (
        <>
            <div>
                <h1>Detail faktury</h1>
                <hr/>
                <h3>Číslo faktury: {invoice.invoiceNumber}</h3>
                <p>
                    <strong>Prodávající:</strong>
                    <br/>
                    {invoice.seller.name}
                </p>
                <p>
                    <strong>Identifikační číslo:</strong>
                    <br/>
                    {invoice.seller.identificationNumber}
                </p>
                <p>
                    <strong>Tax Number</strong>
                    <br/>
                    {invoice.seller.taxNumber}
                </p>
                <p>
                    <strong>číslo účtu:</strong>
                    <br/>
                    {invoice.seller.accountNumber}
                </p>
                <p>
                    <strong>číslo banky:</strong>
                    <br/>
                    {invoice.seller.bankCode}
                </p>
                <p>
                    <strong>iban:</strong>
                    <br/>
                    {invoice.seller.iban}
                </p>
                <p>
                    <strong>Telefonní číslo:</strong>
                    <br/>
                    {invoice.seller.telephone}
                </p>
                <p>
                    <strong>Email:</strong>
                    <br/>
                    {invoice.seller.mail}
                </p>
                <p>
                    <strong>Ulice:</strong>
                    <br/>
                    {invoice.seller.street}
                </p>
                <p>
                    <strong>PSČ:</strong>
                    <br/>
                    {invoice.seller.zip}
                </p>
                <p>
                    <strong>Město:</strong>
                    <br/>
                    {invoice.seller.city}
                </p>
                <p>
                    <strong>Země:</strong>
                    <br/>
                </p>
             
            </div>
        </>
    );
};

export default InvoicesDetail;
