import React, {useEffect, useState} from "react";
import {Link} from "react-router-dom";
import {apiGet, apiPost, apiPut} from "../utils/api";


const InvoicesStatistics = () => {
    const [statistics, setStatistics] = useState();
   
    useEffect(() => {
        apiGet("/api/invoices/statistics").then((data) => setStatistics(data));
    }, []);
   
    

    if(!statistics)
        return <>Loading...</>
    return (
   
        <div> 
            <p>
                <strong>Celková cena za rok:</strong>
                <br/>
                {statistics.currentYearSum}
                <br/>
                <strong>Celková cena za celou dobu:</strong>
                <br/>
                {statistics.allTimeSum}
                <br/>
                <strong>počet faktur:</strong>
                <br/>
                {statistics.invoicesCount}
            </p>

            <table class="table">
            <thead>
                <tr>
                <th scope="col">#</th>
                <th scope="col">First</th>
                <th scope="col">Last</th>
                <th scope="col">Handle</th>
                </tr>
            </thead>
            <tbody>
                <tr>
                <th scope="row">1</th>
                <td>Mark</td>
                <td>Otto</td>
                <td>@mdo</td>
                </tr>
                <tr>
                <th scope="row">2</th>
                <td>Jacob</td>
                <td>Thornton</td>
                <td>@fat</td>
                </tr>
                <tr>
                <th scope="row">3</th>
                <td>Larry</td>
                <td>the Bird</td>
                <td>@twitter</td>
                </tr>
            </tbody>
            </table>
        </div>
        
        
    );
};

export default InvoicesStatistics;
