import { useEffect, useState } from "react";
import { apiGet } from "../utils/api";

function Sales({identificationNumber})
{
    const[invoice,setInvoice] = useState([]);


/*useEffect(() => {
    apiGet("/api/identification/${identificationNumber}/sales}" + id).then((data) => setInvoice(data));
}, [identificationNumber]);*/

return (
    <div>
      <h1>Faktury pro osobu s IČ: {identificationNumber}</h1>
      <ul>
        {invoice.map((invoice) => (
          <li key={invoice._id}>
            <strong>Číslo faktury:</strong> {invoice.invoiceNumber}<br />
            <strong>Prodejce:</strong> {invoice.seller.name}<br />
            <strong>Kupující:</strong> {invoice.buyer.name}<br />
            <strong>Vystaveno:</strong> {invoice.issued}<br />
            <strong>Splatnost:</strong> {invoice.dueDate}<br />
            <strong>Produkt:</strong> {invoice.product}<br />
            <strong>Cena:</strong> {invoice.price} CZK<br />
            <strong>DPH:</strong> {invoice.vat}%<br />
            <strong>Poznámka:</strong> {invoice.note}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Sales;
