import { useEffect, useState } from "react";
import { apiGet } from "../utils/api";

function Sales({identificationNumber,items})
{
   const[invoice,setInvoice] = useState([]);


useEffect(() => {
  apiGet(`/api/identification/${identificationNumber}/sales`).then((data) => setInvoice(data));
}, [identificationNumber]);

return (
    <div>
      <h1>Faktury pro osobu s IČ: {identificationNumber}</h1>
      <ul>
        {i.map((item) => (
          <li key={item._id}>
            <strong>Číslo faktury:</strong> {item.invoiceNumber}<br />
            <strong>Prodejce:</strong> {item.seller.name}<br />
            <strong>Kupující:</strong> {item.buyer.name}<br />
            <strong>Vystaveno:</strong> {item.issued}<br />
            <strong>Splatnost:</strong> {item.dueDate}<br />
            <strong>Produkt:</strong> {item.product}<br />
            <strong>Cena:</strong> {item.price} CZK<br />
            <strong>DPH:</strong> {item.vat}%<br />
            <strong>Poznámka:</strong> {item.note}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Sales;
